WHERE a.id = #{id}

SELECT 

FROM ws_agent_rebate_config a


	a.del_flag = #{DEL_FLAG_NORMAL}

	and a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ws_agent_rebate_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
